import { ADD_FAVORITE, DELETE_FAVORITE, FILTER, ORDER, RESET  } from './action-types'


const initialState = {
    myFavorites: [],
    allCharacters: [],

}


const favoritesReducer = (state= initialState, action) => {
    switch(action.type){
        case ADD_FAVORITE:
            //const addFav = [...state.allCharacters, action.payload]
            return{
                ...state,
                myFavorites: action.payload,
                allCharacters: action.payload
            }
        case DELETE_FAVORITE:
            //let arrayFiltrado = state.myFavorites.filter(character => character.id !== action.payload)
            return{
                ...state,
                myFavorites: action.payload,
                allCharacters : action.payload
                //allCharacters:[...arrayFiltrado]
            }
        case FILTER:
            return{
                ...state,
                myFavorites: state.myFavorites.filter(character => character.gender === action.payload)
            }   

        case ORDER:
            let orderFav;
            if(action.payload==='Ascendente'){
                orderFav = state.myFavorites.sort((a,b)=>
                    a.id > b.id ? 1 : -1
                )
            }else{
                orderFav = state.myFavorites.sort((a,b)=>
                    a.id < b.id ? 1 : -1
                )

            }

            return{
                ...state,
                myFavorites:[...orderFav],
            }

        case RESET:
            return{
                ...state,
                myFavorites: [...state.allCharacters]
            }
            default:
            return {...state}
    }


}

export default favoritesReducer;
