import { ADD_FAVORITE, DELETE_FAVORITE, FILTER, ORDER } from './action-types'
import axios from 'axios'

export const addFavorite = (character) => {

    return async  function( dispatch){ 
        try {
            const response = await axios.post('http://localhost:3001/rickandmorty/create', character)
            dispatch({
                type: ADD_FAVORITE, 
                payload: response.data 
            })

            } catch (error) {
                  return {error: error}
              }

    }
}

export const getAllFavorites = () => {
    return function (dispatch) {
        axios.get('http://localhost:3001/rickandmorty/get')
        .then(response => {
            return dispatch({
                type: 'GET_FAVORITES',
                payload: response.data
            })
        })



    }
}

export const deleteFavorite = (id) => {
    return function (dispatch){
        axios.delete(`http://localhost:3001/rickandmorty/delete/${id}`)

    }

}

export const filterCards = (status) => {
    return{
        type: FILTER, payload: status
    }
}

export const orderCards = (id) => {
    return{
        type: ORDER, payload: id
    }
}
